schemas:
DEFINE TABLE chat_messages SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD message ON chat_messages TYPE string;
DEFINE FIELD created_at ON users TYPE datetime DEFAULT time::now();
DEFINE FIELD updated_at ON users TYPE datetime VALUE time::now();
DEFINE TABLE chats SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD in ON TABLE chats;
DEFINE FIELD out ON TABLE chats;
DEFINE FIELD disabled_by_in ON chats TYPE bool DEFAULT false;
DEFINE FIELD disabled_by_out ON chats TYPE bool DEFAULT false;
DEFINE FIELD created_at ON chats TYPE datetime DEFAULT time::now();
// TODO: Trigger if new messags
DEFINE FIELD updated_at ON chats TYPE datetime VALUE time::now();

DEFINE INDEX unique_relationship 
    ON TABLE chats 
    COLUMNS in, out UNIQUE;

// TODO: Messages count
DEFINE TABLE matches SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD in ON TABLE matches;
DEFINE FIELD out ON TABLE matches;
DEFINE FIELD created_at ON matches TYPE datetime DEFAULT time::now();
DEFINE FIELD updated_at ON matches TYPE datetime VALUE time::now();

DEFINE INDEX unique_relationship 
    ON TABLE matches 
    COLUMNS in, out UNIQUE;

DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE user_images SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD height ON user_images TYPE int;
DEFINE FIELD width ON user_images TYPE int;
DEFINE FIELD url ON user_images TYPE string;
DEFINE FIELD mime_type ON user_images TYPE string;
DEFINE FIELD blurhash ON user_images TYPE string;
DEFINE FIELD file_name ON user_images TYPE string;
DEFINE FIELD is_default ON user_images TYPE bool DEFAULT false;
DEFINE FIELD created_at ON users TYPE datetime DEFAULT time::now();
DEFINE TABLE users SCHEMAFULL PERMISSIONS NONE;
    -- PERMISSIONS
    --     FOR create NONE
    --     FOR select, update, delete WHERE id = $auth.id;

DEFINE FIELD email ON users TYPE string
    ASSERT string::is::email($value);
DEFINE FIELD username ON users TYPE string | null DEFAULT null;
DEFINE FIELD last_name ON users TYPE string | null DEFAULT null;
DEFINE FIELD first_name ON users TYPE string | null DEFAULT null;
DEFINE FIELD birth_date ON users TYPE datetime | null DEFAULT null; 
DEFINE FIELD age ON users TYPE int | null DEFAULT null;
DEFINE FIELD gender ON users TYPE string | null DEFAULT null;
DEFINE FIELD preference ON users TYPE string | null DEFAULT null;
DEFINE FIELD metric ON users TYPE bool DEFAULT true;
DEFINE FIELD height ON users TYPE int | null DEFAULT null;
DEFINE FIELD weight ON users TYPE int | null DEFAULT null;
DEFINE FIELD bio ON users TYPE string | null DEFAULT null;
DEFINE FIELD interests ON users TYPE array<string> | null DEFAULT null;
DEFINE FIELD current_location ON users TYPE point | null DEFAULT null;
DEFINE FIELD max_distance ON users TYPE int DEFAULT 25;
DEFINE FIELD age_range ON users TYPE array<int> | null DEFAULT null;
DEFINE FIELD max_match_count ON users TYPE int DEFAULT 10;
DEFINE FIELD active_hours ON users TYPE array<int> | null DEFAULT null;
DEFINE FIELD has_dog ON users TYPE bool DEFAULT false;
DEFINE FIELD has_cat ON users TYPE bool DEFAULT false;
DEFINE FIELD has_car ON users TYPE bool DEFAULT false;
DEFINE FIELD has_house ON users TYPE bool DEFAULT false;
DEFINE FIELD need_pet ON users TYPE bool DEFAULT false;
DEFINE FIELD need_car ON users TYPE bool DEFAULT false;
DEFINE FIELD need_house ON users TYPE bool DEFAULT false;
DEFINE FIELD open_for_travel_together ON users TYPE bool DEFAULT false;
DEFINE FIELD is_registration_completed ON users TYPE bool DEFAULT false;
DEFINE FIELD is_paid_travel ON users TYPE bool DEFAULT false;
DEFINE FIELD is_verified ON users TYPE bool DEFAULT false;
DEFINE FIELD is_banned ON users TYPE bool DEFAULT false;
DEFINE FIELD created_at ON users TYPE datetime DEFAULT time::now();
DEFINE FIELD updated_at ON users TYPE datetime VALUE time::now();

DEFINE INDEX user_email_index ON TABLE users COLUMNS email UNIQUE;
DEFINE INDEX user_name_index ON TABLE users COLUMNS username UNIQUE;
