schemas:
DEFINE TABLE chat_edge SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD in ON TABLE chat_edge;
DEFINE FIELD out ON TABLE chat_edge;

DEFINE INDEX unique_relationship 
    ON TABLE chat_edge 
    COLUMNS in, out UNIQUE;
DEFINE TABLE chat_message TYPE ANY SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD message ON chat_message TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD reaction ON chat_message TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD is_read ON chat_message TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_deleted ON chat_message TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD read_at ON chat_message TYPE option<datetime> PERMISSIONS FULL;
DEFINE FIELD created_at ON chat_message TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD updated_at ON chat_message TYPE datetime VALUE time::now() PERMISSIONS FULL;

DEFINE TABLE interest TYPE ANY SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD en ON interest TYPE string PERMISSIONS FULL;
DEFINE TABLE interest TYPE ANY SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD en ON interest TYPE string PERMISSIONS FULL;
DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE user TYPE ANY SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD email ON user TYPE string ASSERT string::is::email($value) PERMISSIONS FULL;
DEFINE FIELD username ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD last_name ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD first_name ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD birth_date ON user TYPE option<datetime> PERMISSIONS FULL;
DEFINE FIELD age ON user TYPE option<number> PERMISSIONS FULL;
DEFINE FIELD gender ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD preference ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD goal ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD interests ON user TYPE option<array<string>> PERMISSIONS FULL;
DEFINE FIELD metric ON user TYPE bool DEFAULT true PERMISSIONS FULL;
DEFINE FIELD height ON user TYPE option<number> PERMISSIONS FULL;
DEFINE FIELD weight ON user TYPE option<number> PERMISSIONS FULL;
DEFINE FIELD bio ON user TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD current_location ON user TYPE option<point> PERMISSIONS FULL;
DEFINE FIELD max_distance ON user TYPE number DEFAULT 25 PERMISSIONS FULL;
DEFINE FIELD age_range ON user TYPE option<array<number>> PERMISSIONS FULL;
DEFINE FIELD max_match_count ON user TYPE number DEFAULT 10 PERMISSIONS FULL;
DEFINE FIELD has_pet ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD has_car ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD has_house ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD need_pet ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD need_car ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD need_house ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD open_for_travel_together ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_registration_completed ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_paid_travel ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_verified ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_banned ON user TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD created_at ON user TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD updated_at ON user TYPE datetime VALUE time::now() PERMISSIONS FULL;

DEFINE INDEX user_email_index ON user FIELDS email UNIQUE;
DEFINE INDEX user_name_index ON user FIELDS username UNIQUE;
DEFINE TABLE user_edge TYPE ANY SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD in ON user_edge PERMISSIONS FULL;
DEFINE FIELD out ON user_edge PERMISSIONS FULL;
DEFINE FIELD in_swipe ON user_edge TYPE option<bool> PERMISSIONS FULL;
DEFINE FIELD out_swipe ON user_edge TYPE option<bool> PERMISSIONS FULL;
DEFINE FIELD is_super_swipe ON user_edge TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD is_unmatched ON user_edge TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD unmatched_at ON user_edge TYPE option<datetime> PERMISSIONS FULL;
DEFINE FIELD messages_count ON user_edge TYPE int DEFAULT 0;
DEFINE FIELD unread_messages_count ON user_edge TYPE int DEFAULT 0;
DEFINE FIELD chat_disabled_by_in ON user_edge TYPE bool DEFAULT false;
DEFINE FIELD chat_disabled_by_out ON user_edge TYPE bool DEFAULT false;
DEFINE FIELD created_at ON user_edge TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD updated_at ON user_edge TYPE datetime VALUE time::now() PERMISSIONS FULL;

DEFINE INDEX unique_relationship ON user_edge FIELDS in, out UNIQUE;

DEFINE TABLE user_image SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD height ON user_image TYPE int;
DEFINE FIELD width ON user_image TYPE int;
DEFINE FIELD url ON user_image TYPE string;
DEFINE FIELD mime_type ON user_image TYPE string;
DEFINE FIELD blurhash ON user_image TYPE string;
DEFINE FIELD file_name ON user_image TYPE string;
DEFINE FIELD is_default ON user_image TYPE bool DEFAULT false;
DEFINE FIELD created_at ON user_image TYPE datetime DEFAULT time::now();
